INSERT INTO BandCompetency(bandName, competencyName, description, competencyMainName)
values
('Manager', 'Mobilises people to drive business improvement & innovation', 'Supports others in delivering high quality services and excellence by supporting mobilising teams and contributing to business improvement.', 'Working with Others'),
('Manager', 'Champions continuous improvement in self & others – creating a positive environment and encouraging contribution', 'Seeks and acknowledges the views and inputs of others. Shows respect for the contributions and challenges of others through positive and constructive feedback.', 'Working with Others'),
('Manager', 'Managing performance', 'Uses information and data about performance to identify improvements which will strengthen services.', 'Working with Others'),
('Manager', 'Developing networks and building and maintaining relationships', 'Identifies where working and cooperating with others can result in better services. Endeavours to work collaboratively. Communicates with and listens to others, recognising different perspectives. Empathises and considers the needs and feelings of others. Gains and maintains trust and support.', 'Working with Others'),
('Manager', 'Working within teams', 'Understands roles, responsibilities, and purpose within the team. Adopts a collaborative approach and respects team decisions.', 'Working with Others'),
('Principal', 'Mobilises people to drive business improvement & innovation', 'Motivates and coaches’ individuals and teams to strengthen their performance and assist with developing their own capabilities and skills. Aligns individual development needs with service goals.', 'Working with Others'),
('Principal', 'Champions continuous improvement in self & others – creating a positive environment and encouraging contribution', 'Integrates the contributions of a diverse range of stakeholders, being open and honest about the extent to which contributions can be based upon. Challenges others to provide stronger feedback and drive continuous improvement.', 'Working with Others'),
('Principal', 'Managing performance', 'Establishes rigorous performance measures. Holds self, individuals, and teams to account for achieving performance standards. Challenges when service expectations are not being met and takes corrective action.', 'Working with Others'),
('Principal', 'Developing networks and building and maintaining relationships', 'Identifies and builds effective networks with a range of influential stakeholders internal and external to the organisation. Builds and nurtures trusting relationships at all levels within and across services and organisational boundaries.', 'Working with Others'),
('Principal', 'Working within teams', 'Takes on recognised leadership roles within the organisation. Builds high performing inclusive teams that contribute to productivity and efficiencies. Promotes autonomy and empowerment and maintains a sense of optimism and confidence.', 'Working with Others'),
('Leadership Community', 'Mobilises people to drive business improvement & innovation', 'Inspires and supports the business to mobilise diverse teams that are committed to and aligned with organisational values and goals. Engages with influencers to deliver joined up services.', 'Working with Others'),
('Leadership Community', 'Champions continuous improvement in self & others – creating a positive environment and encouraging contribution', 'Creates systems which encourage contribution throughout the organisation. Invites contribution from different regions, sectors, and business units to bring about consistent improvements.', 'Working with Others'),
('Leadership Community', 'Managing performance', 'Successfully accountable for the direction of the wider business on creating performance standards and recognises business areas that require focus and adapts approach to meet the demands and needs of employees, customers, and services.', 'Working with Others'),
('Leadership Community', 'Developing networks and building and maintaining relationships', 'Works across boundaries creating networks which facilitate high levels of collaboration within and across organisations and sectors. Builds and maintains sustainable strategic alliances across the system and other sectors. Has high impact when interacting with others at all levels.', 'Working with Others'),
('Leadership Community', 'Working within teams', 'Contributes and leads senior teams. Enables others to take on leadership responsibilities, building high level leadership capability and capacity from a diverse range of backgrounds.', 'Working with Others');
INSERT INTO BandCompetency(bandName, competencyName, description, competencyMainName)
values
('Manager', 'Identifying the contexts for change', 'Understands the range of factors which determine why changes are made and supports Kainos senior leaders to deliver key messages.', 'Setting Direction, Development and Accountability'),
('Manager', 'Improvement and Innovation', 'Gathers data and information about aspects of the business, analyses evidence and uses this knowledge to suggest changes that will improve services in the future. Questions established practices which do not add value. Puts forward creative suggestions to improve the quality of service provided.', 'Setting Direction, Development and Accountability'),
('Manager', 'Making decisions', 'Consults with others and contributes to decisions including the future direction/vision of own business area or projects.', 'Setting Direction, Development and Accountability'),
('Manager', 'Creating and Evaluating impact', 'Assesses the effects of change on service delivery and customer outcomes. Makes recommendations for future improvements.', 'Setting Direction, Development and Accountability'),
('Principal', 'Identifying the contexts for change', 'Actively seeks to learn about internal and external factors which will impact on the business. Interprets the meaning of these for Kainos and incorporates these into business plans and actions.', 'Setting Direction, Development and Accountability'),
('Principal', 'Improvement and Innovation', 'Understands the complex interdependencies across a range of services. Applies knowledge to set future direction. Challenges colleagues’ thinking to find better and more effective ways of delivering services and quality. Accesses creativity and innovation from relevant individuals and groups.', 'Setting Direction, Development and Accountability'),
('Principal', 'Making decisions', 'Remains accountable for making timely decisions in complex situations. Modifies decisions and flexes direction when faced with new information or changing circumstances', 'Setting Direction, Development and Accountability'),
('Principal', 'Creating and Evaluating impact', 'Identifies gains which can be applied elsewhere in the business and incorporates these into operational / capability / business plans. Disseminates learning from changes which have been introduced.', 'Setting Direction, Development and Accountability'),
('Leadership Community', 'Identifying the contexts for change', 'Synthesises knowledge from a broad range of sources to identify future challenges and imperatives that will create the need for change. Influences the context for change in the best interests of our people, our customers, and our business.', 'Setting Direction, Development and Accountability'),
('Leadership Community', 'Improvement and Innovation', 'Uses knowledge, evidence,and experience of national and international developments in technology and our consumers to influence the future development of Kainos and our services. Drives a culture of innovation and improvement. Integrates radical and innovative approaches into strategic plans to make Kainos world class in the provision of technology services.', 'Setting Direction, Development and Accountability'),
('Leadership Community', 'Making decisions', 'Ensures that corporate decision-making is conducted with appropriate rigour and takes account of the full range of factors impinging on the future direction of the organisation and the wider business. Can operate without all the facts. Takes unpopular decisions when in the best interests of Kainos in the long term.', 'Setting Direction, Development and Accountability'),
('Leadership Community', 'Creating and Evaluating impact', 'Synthesises learning arising from changes which have been introduced and incorporates these into strategic plans. Shares learning with the wider business.', 'Setting Direction, Development and Accountability');
INSERT INTO BandCompetency(bandName, competencyName, description, competencyMainName)
values
('Manager', 'Framing the strategy', 'Strategic awareness including an understanding and knowledge of how role and those within in immediate team fit with and support delivery of the organisational objectives.', 'Supporting and Delivering the Strategy'),
('Manager', 'Developing the strategy', 'Feeds in ideas and knowledge from immediate teams and supports the senior leaders in developing a strategy.', 'Supporting and Delivering the Strategy'),
('Manager', 'Implementing the strategy', 'Support, develop and implement the strategy in immediate teams.', 'Supporting and Delivering the Strategy'),
('Manager', 'Embedding the strategy', 'Passionately advocates the strategy and vision and translates this into action and opportunities within immediate teams. Understands the need for OKR’s/KPI’s and own role in supporting and shaping in relation to immediate teams.', 'Supporting and Delivering the Strategy'),
('Principal', 'Framing the strategy', 'Encourages and demonstrates creative, lateral, critical and systems-based thinking when framing the strategy.', 'Supporting and Delivering the Strategy'),
('Principal', 'Developing the strategy', 'Engages with key individuals and groups to formulate strategic plans that aligns with the wider Kainos objectives. Identify organisational change requirements and ensure they are understood for organisational readiness decisions.', 'Supporting and Delivering the Strategy'),
('Principal', 'Implementing the strategy', 'Ensures that the strategic plans are translated into workable operational plans, identifying risks, critical success factors and evaluation methods. Championing change despite personal resistance to demonstrate the ability to support and lead other through change.', 'Supporting and Delivering the Strategy'),
('Principal', 'Embedding the strategy', 'Creates a consultative organisational culture to support delivery of the strategy and to drive strategic change within the wider business. Sets direction and priorities through KPI’s and monitors success.', 'Supporting and Delivering the Strategy'),
('Leadership Community', 'Framing the strategy', 'Takes account of the culture, history, and long-term strategic direction for Kainos. Critically reviewing relevant thinking, ideas and best practice and applies whole systems thinking to conceptualise a strategy in line with the vision.', 'Supporting and Delivering the Strategy'),
('Leadership Community', 'Developing the strategy', 'Integrates the views of a broad range of stakeholders to develop a coherent, joined up and sustainable strategy for own domain. Assesses organisational readiness for change. Manages the risks, political sensitivities and environmental uncertainties involved.', 'Supporting and Delivering the Strategy'),
('Leadership Community', 'Implementing the strategy', 'Responds constructively to challenge. Puts systems, structures, processes, resources, and plans in place to deliver the strategy. Establishes accountabilities and holds people in local, regional, and national structures to account for jointly delivering strategic and operational plans. Demonstrates flexibility when change is required.', 'Supporting and Delivering the Strategy'),
('Leadership Community', 'Embedding the strategy', 'Enables and supports the conditions and culture needed to sustain changes integral to the successful delivery of the strategy. Keeps momentum alive by reinforcing key messages, monitoring progress, and recognising where the strategy has been embraced by others. Evaluates outcomes and uses learnings to adapt strategic and operational plans. Aligns KPI’s across the business and reports on progress to ensure continued progress.', 'Supporting and Delivering the Strategy');
INSERT INTO BandCompetency(bandName, competencyName, description, competencyMainName)
values
('Manager', 'Demonstrates commercial awareness and behaviours', 'Aware of the importance of effective commercial behaviours and the needs of the business. Identifies and ensures that personal objectives are focussed on innovative solutions to achieve commercial outcomes and objectives as well as contributing to our growth ambitions. Recognises when products or services are not being  delivered to the required level of quality or standard and takes appropriate action.', 'Commerciality and Risk'),
('Manager', 'Contributes to the ongoing development of new business to support Kainos’ growth ambitions', 'Contributes to development of new business through involvement in BD process, for example, bid/proposal writing, customer presentations, pricing solutions. Establishes trusted customer relationships and adds value by helping and advising customers in areas beyond current project work, identifying possible areas where additional Kainos business can be generated.', 'Commerciality and Risk'),
('Manager', 'Improves efficiency and effectiveness to drive profitability', 'Looks to support and drive efficiencies and profitability through immediate team. Reviews processes within own teams and challenges through the right behaviours and channels to support the overall commercial objectives.', 'Commerciality and Risk'),
('Manager', 'Manages risk to Kainos and the interest of our shareholders', 'Understands and protects Kainos from risks associated with contractual commitments. Takes a balanced risk and reward view to activities and commitments while managing quality and Kainos interests.', 'Commerciality and Risk'),
('Manager', 'Promotes and adheres to Kainos process and policies', 'Supports and aligns to Kainos policies and processes escalating or challenging where appropriate. Ensures compliance across own team.', 'Commerciality and Risk'),
('Principal', 'Demonstrates commercial awareness and behaviours', 'Recognises commercial opportunities and where necessary seeks support in converting those opportunities into income. Leads on the awareness to protect margin through various means by setting the tone within own teams and BU. Encouraging others to challenge profit, spend and process. Creates value add propositions to support growth and negotiates fair deals for customers and services both internal and external.', 'Commerciality and Risk'),
